import React, {Component} from "react"
import {Redirect} from 'react-router-dom'
import axios from 'axios'

import LinkInClass from "../components/LinkInClass"

export default class Login extends Component
{
    constructor(props)
    {
        super(props)
        this.state = ({isLoggedIn:false}) 
    }


    handleSubmit = (e) => 
    {
        e.preventDefault()

        axios.defaults.withCredentials = true; // needed for sessions to work
        axios.post('http://localhost:4000/users/login/a@a.com/123')
        .then(res => 
        {     
            if(res.data)
            {
                if (res.data.errorMessage)
                {
                    console.log(res.data.errorMessage)    
                }
                else
                { 
                    console.log(res.data)
                    console.log("User logged in")
                    sessionStorage.user.isLoggedIn = "true";
                    sessionStorage.user.name = res.data.name;
                    sessionStorage.user.accessLevel = res.data.accessLevel
                    this.setState({isLoggedIn:true})
                }        
            }
            else
            {
                console.log(`Login failed`)
            }
        })        
    }


    render()
    {
        return (
                <div className="form-container">  

                {this.state.isLoggedIn ? <Redirect to="/DisplayAllCars"/> : null} 

                    <LinkInClass value="Log in" className="green-button" onClick={this.handleSubmit}/>                          
                </div>
               )
        }
}
