import React, {Component} from "react"
import {Redirect} from 'react-router-dom'
import axios from 'axios'

import LinkInClass from "../components/LinkInClass"
import {ACCESS_LEVEL_GUEST} from "../constants"


export default class Register extends Component
{
    constructor(props)
    {
        super(props)
        this.state = ({name:"",
                       email:"",
                       password:"",
                       accessLevel:0,
                       isRegistered:false}) 
    }


    handleChange = (e) => 
    {
        this.setState({[e.target.name]: e.target.value})
        console.log("state: " + this.state)
    }


    handleSubmit = (e) => 
    {
        e.preventDefault()
        console.log("name:" + this.state.name) 

        let x = 0
        if(x > 0)
        {
        axios.defaults.withCredentials = true // needed for sessions to work
        axios.post(`http://localhost:4000/users/register/${this.state.name}/${this.state.email}/${this.state.password}/${this.state.accessLevel}`)
        .then(res => 
        {     
            // default if not logged in
            sessionStorage.name = "GUEST"
            sessionStorage.accessLevel = ACCESS_LEVEL_GUEST // guest has accessLevel of 0
            if(res.data)
            {
                if (res.data.errorMessage)
                {
                    console.log(res.data.errorMessage)    
                }
                else // user successfully logged in
                { 
                    console.log("User logged in")

                    sessionStorage.isLoggedIn = "true"
                    sessionStorage.name = res.data.name
                    sessionStorage.accessLevel = res.data.accessLevel

                    this.setState({isRegistered:true})
                }        
            }
            else
            {
                console.log(`Login failed`)
            }
        })   
    }
    }


    render()
    {
        return (
                <div className="form-container">  

                    {this.state.isRegistered ? <Redirect to="/DisplayAllCars"/> : null} 

                    <input 
                        type = "text" 
                        name = "name" 
                        placeholder = "Name"
                    /><br/>                    
                    <input 
                        type = "email" 
                        name = "email" 
                        placeholder = "Email"
                    /><br/>
                    <input 
                        type = "text" 
                        name = "password" 
                        placeholder = "Password"
                    /><br/>

                    <input 
                        type = "number" 
                        name = "accessLevel" 
                        placeholder = "Access Level"
                    /><br/>

                    <LinkInClass value="Register" className="green-button" onClick={this.handleSubmit}/> 


                </div>
               )
        }
}
