import React, {Component} from "react"
import {Redirect} from 'react-router-dom'
import axios from 'axios'

import LinkInClass from "../components/LinkInClass"
import {ACCESS_LEVEL_NORMAL_USER} from "../constants"


export default class Register extends Component
{
    constructor(props)
    {
        super(props)
        this.state = ({name:"",
                       email:"",
                       password:"",
                       accessLevel:ACCESS_LEVEL_NORMAL_USER,     
                       isRegistered:false}) 
    }


    handleChange = (e) => 
    {
        this.setState({[e.target.name]: e.target.value})
    }


    handleSubmit = (e) => 
    {
        e.preventDefault()

        axios.defaults.withCredentials = true // needed for sessions to work
        axios.post(`${SERVER_HOST}/users/register/${this.state.name}/${this.state.email}/${this.state.password}/${this.state.accessLevel}`)
        .then(res => 
        {     
            if(res.data)
            {
                if (res.data.errorMessage)
                {
                    console.log(res.data.errorMessage)    
                }
                else // user successfully registered
                { 
                    console.log("User registered and logged in")

                    sessionStorage.isLoggedIn = "true"
                    sessionStorage.name = res.data.name
                    sessionStorage.accessLevel = res.data.accessLevel

                    this.setState({isRegistered:true})
                }        
            }
            else
            {
                console.log(`Registration failed`)
            }
        })   
    }

    resetUserCollection = () =>
    {
        axios.defaults.withCredentials = true // needed for sessions to work
        axios.post(`${SERVER_HOST}/users/reset_user_collection`)
        .then(res => 
        {     
            if(res.data)
            {
                if (res.data.errorMessage)
                {
                    console.log(res.data.errorMessage)    
                }
                else // user successfully reset the User collection
                { 
                    console.log("User collection reset")
                }        
            }
            else
            {
                console.log(`Failed to reset User collection`)
            }
        })   

    }


    render()
    {
        return (
                <div className="form-container">  

                    {this.state.isRegistered ? <Redirect to="/DisplayAllCars"/> : null} 

                    <input 
                        type = "text" 
                        name = "name" 
                        placeholder = "Name"
                        value={this.state.name} 
                        onChange={this.handleChange}
                    /><br/>              

                    <input 
                        type = "email" 
                        name = "email" 
                        placeholder = "Email"
                        value={this.state.email} 
                        onChange={this.handleChange}
                    /><br/>

                    <input 
                        type = "text" 
                        name = "password" 
                        placeholder = "Password"
                        value={this.state.password} 
                        onChange={this.handleChange}
                    /><br/>

                    <input 
                        type = "number" 
                        name = "accessLevel" 
                        placeholder = "Access Level"
                        min="1"
                        max="2"
                        value={this.state.accessLevel} 
                        onChange={this.handleChange}
                    /><br/>

                    <LinkInClass value="Register" className="green-button" onClick={this.handleSubmit}/> 
                    <br/><br/><br/><br/><LinkInClass value="Reset User Database" className="red-button" onClick={this.resetUserCollection}/>                  
                </div>
               )
        }
}
