import React, {Component} from "react"
import Form from 'react-bootstrap/Form'
import {Redirect, Link} from 'react-router-dom'
import axios from 'axios'

import ButtonAsLink from "../components/ButtonAsLink"

export default class EditCar extends Component 
{
    constructor(props) 
    {
        super(props)

        this.state = {
            model: '',
            colour: '',
            year: '',
            price: ''
        }
    }

componentDidMount() 
{      
    this.inputToFocus.focus()

    axios.get('http://localhost:4000/cars/get_car/' + this.props.match.params.id)
         .then(res => {

              if(res.data)
              {
                  this.setState({
                      model: res.data.model,
                      colour: res.data.colour,
                      year: res.data.year,
                      price: res.data.price,
                      redirectToDisplayAllCars:false
                  })
              }
              else
              {
                  console.log(`Record not found`)
              }
           })
           .catch(error => console.log(error))
}


handleChange = (e) => 
{
    this.setState({[e.target.name]: e.target.value})
}


handleSubmit = (e) => 
{
    e.preventDefault()

    const carObject = {
        model: this.state.model,
        colour: this.state.colour,
        year: this.state.year,
        price: this.state.price
    }

    axios.put('http://localhost:4000/cars/update_car/' + this.props.match.params.id, carObject)
            .then(res => 
            { 
                if(res.data)
                {
                    console.log(`Record updated`)
                    this.setState({redirectToDisplayAllCars:true})
                }
                else
                {
                    console.log("Record not updated")
                }})
                .catch((error) => {console.log(error)})
}


render() 
{
    return (
            <div className="form-container">

              {this.state.redirectToDisplayAllCars ? <Redirect to="/DisplayAllCars"/> : null}  

              <Form onSubmit={this.handleSubmit}>
                <Form.Group controlId="model">
                  <Form.Label>Model</Form.Label>
                  <Form.Control ref = {(input) => { this.inputToFocus = input }} type="text" name="model" value={this.state.model} onChange={this.handleChange} />
                </Form.Group>

                <Form.Group controlId="colour">
                  <Form.Label>Colour</Form.Label>
                  <Form.Control type="text" name="colour" value={this.state.colour} onChange={this.handleChange} />
                </Form.Group>

                <Form.Group controlId="year">
                  <Form.Label>Year</Form.Label>
                  <Form.Control type="text" name="year" value={this.state.year} onChange={this.handleChange} />
                </Form.Group>

                <Form.Group controlId="price">
                  <Form.Label>Price</Form.Label>
                  <Form.Control type="text" name="price" value={this.state.price} onChange={this.handleChange} />
                </Form.Group>

                <input type="button" className="green-button" value = "Update" onClick={this.handleSubmit}/>


                   <Link to="/DisplayAllCars">
     <button type="button" className="red-button" >
          Cancel
     </button>
 </Link>



              <ButtonAsLink value="Test" to='/DisplayAllCars' className="green-button"/>  





                <Link className="red-button" to={"/DisplayAllCars"}>
                  Cancel
                </Link>
              </Form>
            </div>
           )
  }
}
