import { Link } from 'react-router-dom';
import axios from 'axios';
import CarTable from './CarTable';


export default class DisplayAllCars extends Component 
{
    constructor(props) 
    {
        super(props)
        this.state = {
            loggedIn:localStorage.loggedIn,
            cars: []
        };    
         console.log('logged in: ' + localStorage.loggedIn)
        // timer is needed to allow the database change to take effect before getting the results
        setTimeout(() => {axios.get('http://localhost:4000/cars/')
                               .then(res => this.setState({cars: res.data}),
                               ((error) => console.log(error)), 200)}); // wait 200 milliseconds
  }


  render() {
      localStorage.loggedIn = false;
      let temp = localStorage.loggedIn;
    let content = <div><CarTable cars={this.state.cars} /> <div className="add-new-car"><Link className="blue-button" to={"/AddCar"}>Add New Car</Link></div></div>
if(temp === false)
{
    content = <div>No Access</div>
}
