import React, {Component} from "react"
//import {Redirect} from 'react-router-dom'
import axios from 'axios'

import LinkInClass from "../components/LinkInClass"
import {ACCESS_LEVEL_GUEST} from "../constants"


export default class Register extends Component
{
    constructor(props)
    {
        super(props)
        this.state = ({name:"",
                       email:"",
                       password:"",
                       accessLevel:0,
                       isRegistered:false}) 
    }


    handleSubmit = (e) => 
    {
        e.preventDefault()

        axios.defaults.withCredentials = true // needed for sessions to work
        axios.post(`http://localhost:4000/users/register/${this.state.name}/${this.state.email}/${this.state.password}/${this.state.accessLevel}`)
        .then(res => 
        {     
            // default if not logged in
            sessionStorage.name = "GUEST"
            sessionStorage.accessLevel = ACCESS_LEVEL_GUEST // guest has accessLevel of 0
            if(res.data)
            {
                if (res.data.errorMessage)
                {
                    console.log(res.data.errorMessage)    
                }
                else // user successfully logged in
                { 
                    console.log("User logged in")

                    sessionStorage.isLoggedIn = "true"
                    sessionStorage.name = res.data.name
                    sessionStorage.accessLevel = res.data.accessLevel

                    this.setState({isRegistered:true})
                }        
            }
            else
            {
                console.log(`Login failed`)
            }
        })        
    }


    render()
    {
        return (
                <div className="form-container">  

                    hello

                </div>
               )
        }
}
