//Author: Derek O Reilly
//
// Helper Component class that allows us to have a button that render the same way as a <Link> component
// Use this class only open a route if the user is logged in
// Use <Route> for routes that are not private

import React from "react";
import {Route, Redirect } from "react-router-dom";

export default class PrivateRoute extends Component
{
    render()
    {
    <Route
        exact = {props.exact}
        path = {props.path}
        render = {props => sessionStorage.isLoggedIn==="true" ? <Component {...props} {...rest} /> : <Redirect to="/DisplayAllCars"/> }
    />
    }
}
