//Author: Derek O Reilly
//
// Helper Component class that allows us to have a button that render the same way as a <Link> component
// Use this class only open a route if the user is logged in
// Use <Route> for routes that are not private

import React, { Component, Fragment } from "react";
import { withRouter } from "react-router-dom";
import Login from "./Login";
import Header from "./Header";

export default class RequireAuth extends Component 
    {

  render = () =>
    !sessionStorage.isLoggedIn ? (
      <Redirect to="/DisplayAllCars" />
    ) : (
      <Fragment>

        {this.props.children}
      </Fragment>
    );
}
